#!/usr/bin/env bun

/**
 * {{helper:pascalCase name}} CLI
 *
 * {{description}}
 *
 * @author {{author}}
 * @license {{license}}
 * @version 1.0.0
 * @since {{year}}
 */

import { Command } from 'commander';
import chalk from 'chalk';
import ora from 'ora';
import { hello } from '../index.js';

const program = new Command();

program
  .name('{{name}}')
  .description('{{description}}')
  .version('1.0.0');

// Hello command
program
  .command('hello')
  .description('Say hello to someone')
  .argument('[name]', 'Name to greet', 'World')
  .option('-c, --capitalize', 'Capitalize the greeting')
  .action(async (name: string, options: { capitalize?: boolean }) => {
    const spinner = ora('Processing greeting...').start();

    try {
      // Simulate some async processing
      await new Promise(resolve => setTimeout(resolve, 1000));

      const greeting = hello(name);
      const finalGreeting = options.capitalize ? greeting.toUpperCase() : greeting;

      spinner.succeed('Done!');
      console.log(chalk.green(finalGreeting));
    } catch (error) {
      spinner.fail('Error occurred');
      console.error(chalk.red('Failed to process greeting:'), error);
      process.exit(1);
    }
  });

// Status command
program
  .command('status')
  .description('Show application status')
  .action(() => {
    console.log(chalk.blue('{{helper:pascalCase name}} CLI Status'));
    console.log(chalk.gray('─'.repeat(30)));
    console.log(`Version: ${chalk.yellow('1.0.0')}`);
    console.log(`Environment: ${chalk.yellow(process.env.NODE_ENV || 'development')}`);
    console.log(`Platform: ${chalk.yellow(process.platform)}`);
    console.log(`Node.js: ${chalk.yellow(process.version)}`);
  });

// Global error handling
program.exitOverride();

process.on('uncaughtException', (error) => {
  console.error(chalk.red('Uncaught Exception:'), error);
  process.exit(1);
});

process.on('unhandledRejection', (reason, promise) => {
  console.error(chalk.red('Unhandled Rejection at:'), promise, 'reason:', reason);
  process.exit(1);
});

// Parse command line arguments
program.parse();

export { main } from './cli-entry-point.js';

/**
 * {{helper:pascalCase name}} CLI Entry Point
 *
 * Main CLI function for {{name}}
 *
 * @author {{author}}
 * @license {{license}}
 * @version 1.0.0
 * @since {{year}}
 */

import program from './index.js';

/**
 * Main CLI function that handles command line arguments
 * @param {string[]} args - Command line arguments
 */
export async function main(args: string[]): Promise<void> {
  try {
    // Parse command line arguments
    await program.parseAsync(args);
  } catch (error) {
    console.error('CLI Error:', error);
    process.exit(1);
  }
}

/**
 * {{helper:pascalCase name}} CLI Library
 *
 * {{description}}
 *
 * @author {{author}}
 * @license {{license}}
 * @version 1.0.0
 * @since {{year}}
 */

export const VERSION = '1.0.0';

export interface HelloOptions {
  uppercase?: boolean;
  exclamation?: boolean;
}

/**
 * Generate a greeting message
 *
 * @param name - Name to greet
 * @param options - Additional options for formatting
 * @returns Formatted greeting message
 */
export function hello(name: string = 'World', options: HelloOptions = {}): string {
  let greeting = `Hello, ${name}`;

  if (options.uppercase) {
    greeting = greeting.toUpperCase();
  }

  if (options.exclamation) {
    greeting += '!';
  } else {
    greeting += '.';
  }

  return greeting;
}

/**
 * Main CLI function
 */
export async function main(): Promise<void> {
  console.log('{{helper:pascalCase name}} CLI - Version', VERSION);
  console.log('{{description}}');
}

export default {
  VERSION,
  hello,
  main
};

// ESLint configuration for {{name}}
// Quality level: {{qualityLevel}}

import eslint from '@eslint/js';
import typescriptPlugin from '@typescript-eslint/eslint-plugin';
import typescriptParser from '@typescript-eslint/parser';

export default [
  eslint.configs.recommended,
  {
    files: ['**/*.ts'],
    languageOptions: {
      parser: typescriptParser,
      parserOptions: {
        ecmaVersion: 'latest',
        sourceType: 'module',
      },
    },
    plugins: {
      '@typescript-eslint': typescriptPlugin,
    },
    rules: {
      '@typescript-eslint/no-unused-vars': {{#if (eq qualityLevel 'strict')}}'error'{{else}}{{#if (eq qualityLevel 'medium')}}'warn'{{else}}'off'{{/if}}{{/if}},
      '@typescript-eslint/no-explicit-any': {{#if (eq qualityLevel 'strict')}}'error'{{else}}{{#if (eq qualityLevel 'medium')}}'warn'{{else}}'off'{{/if}}{{/if}},
      '@typescript-eslint/prefer-const': 'error',
      'prefer-const': 'error',
      'no-var': 'error',
      {{#if (eq qualityLevel 'strict')}}
      'no-console': 'warn',
      'no-debugger': 'error',
      {{/if}}
    },
  },
  {
    files: ['tests/**/*.ts'],
    rules: {
      '@typescript-eslint/no-explicit-any': 'off',
      'no-console': 'off',
    },
  },
  {
    ignores: [
      'dist/',
      'node_modules/',
      'coverage/',
      '*.config.js',
      '*.config.mjs',
    ],
  },
];

/**
 * Unit tests for {{name}}
 */

import { describe, it, expect, beforeEach } from 'bun:test';
import { hello, VERSION } from '../src/index';

describe('{{name}}', () => {
  describe('hello function', () => {
    it('should return greeting with default name', () => {
      expect(hello()).toBe('Hello, World.');
    });

    it('should return greeting with custom name', () => {
      expect(hello('TypeScript')).toBe('Hello, TypeScript.');
    });

    it('should capitalize greeting when uppercase option is true', () => {
      expect(hello('TypeScript', { uppercase: true })).toBe('HELLO, TYPESCRIPT.');
    });

    it('should add exclamation when exclamation option is true', () => {
      expect(hello('TypeScript', { exclamation: true })).toBe('Hello, TypeScript!');
    });

    it('should apply both options together', () => {
      expect(hello('TypeScript', { uppercase: true, exclamation: true })).toBe('HELLO, TYPESCRIPT!');
    });
  });

  describe('VERSION', () => {
    it('should have correct version', () => {
      expect(VERSION).toBe('1.0.0');
    });
  });
});

/**
 * Integration tests for CLI commands
 */

import { describe, it, expect, beforeEach } from 'bun:test';
import { spawn } from 'child_process';
import path from 'path';
import fs from 'fs';

const cliPath = path.join(__dirname, '../dist/cli/index.js');

describe('CLI Commands', () => {
  beforeEach(async () => {
    // Ensure CLI is built before tests
    if (!fs.existsSync(cliPath)) {
      await new Promise((resolve, reject) => {
        const build = spawn('bun', ['run', 'build'], { stdio: 'inherit' });
        build.on('close', (code) => {
          if (code === 0) resolve(undefined);
          else reject(new Error(`Build failed with code ${code}`));
        });
      });
    }
  });

  describe('hello command', () => {
    it('should greet with default name', async () => {
      const result = await runCli(['hello']);
      expect(result.stdout).toContain('Hello, World');
      expect(result.code).toBe(0);
    }, 10000);

    it('should greet with custom name', async () => {
      const result = await runCli(['hello', 'TypeScript']);
      expect(result.stdout).toContain('Hello, TypeScript');
      expect(result.code).toBe(0);
    }, 10000);

    it('should capitalize when flag is provided', async () => {
      const result = await runCli(['hello', 'TypeScript', '--capitalize']);
      expect(result.stdout).toContain('HELLO, TYPESCRIPT');
      expect(result.code).toBe(0);
    }, 10000);
  });

  describe('status command', () => {
    it('should show status information', async () => {
      const result = await runCli(['status']);
      expect(result.stdout).toContain('{{helper:pascalCase name}} CLI Status');
      expect(result.stdout).toContain('Version:');
      expect(result.stdout).toContain('Environment:');
      expect(result.code).toBe(0);
    }, 10000);
  });
});

interface CliResult {
  code: number | null;
  stdout: string;
  stderr: string;
}

function runCli(args: string[]): Promise<CliResult> {
  return new Promise((resolve) => {
    const cli = spawn('node', [cliPath, ...args]);
    let stdout = '';
    let stderr = '';

    cli.stdout?.on('data', (data) => {
      stdout += data.toString();
    });

    cli.stderr?.on('data', (data) => {
      stderr += data.toString();
    });

    cli.on('close', (code) => {
      resolve({ code, stdout, stderr });
    });

    cli.on('error', (error) => {
      stderr += error.toString();
      resolve({ code: 1, stdout, stderr });
    });
  });
}
