/**
 * {{helper:pascalCase name}} Web Server
 *
 * {{description}}
 *
 * @author {{author}}
 * @license {{license}}
 * @version 1.0.0
 * @since {{year}}
 */

import express from 'express';
import cors from 'cors';
import helmet from 'helmet';
import morgan from 'morgan';
import dotenv from 'dotenv';
import { createApiRouter } from './routes/api.js';
import { errorHandler } from './middleware/errorHandler.js';
import { notFoundHandler } from './middleware/notFoundHandler.js';

// Load environment variables
dotenv.config();

const app = express();
const PORT = process.env.PORT || 3000;
const NODE_ENV = process.env.NODE_ENV || 'development';

// Security middleware
app.use(helmet());
app.use(cors({
  origin: NODE_ENV === 'production' ? false : true,
  credentials: true
}));

// Logging middleware
if (NODE_ENV === 'development') {
  app.use(morgan('dev'));
} else {
  app.use(morgan('combined'));
}

// Body parsing middleware
app.use(express.json({ limit: '10mb' }));
app.use(express.urlencoded({ extended: true, limit: '10mb' }));

// Health check endpoint
app.get('/health', (req, res) => {
  res.status(200).json({
    status: 'ok',
    timestamp: new Date().toISOString(),
    uptime: process.uptime(),
    environment: NODE_ENV,
    version: '1.0.0'
  });
});

// API routes
app.use('/api', createApiRouter());

// Serve static files in production
if (NODE_ENV === 'production') {
  app.use(express.static('public'));
}

// Error handling middleware
app.use(notFoundHandler);
app.use(errorHandler);

// Start server
const server = app.listen(PORT, () => {
  console.log(`ðŸš€ {{helper:pascalCase name}} server running on port ${PORT}`);
  console.log(`ðŸ“ Environment: ${NODE_ENV}`);
  console.log(`ðŸ¥ Health check: http://localhost:${PORT}/health`);
  if (NODE_ENV === 'development') {
    console.log(`ðŸ”— API docs: http://localhost:${PORT}/api`);
  }
});

// Graceful shutdown
process.on('SIGTERM', () => {
  console.log('SIGTERM received, shutting down gracefully');
  server.close(() => {
    console.log('Process terminated');
    process.exit(0);
  });
});

process.on('SIGINT', () => {
  console.log('SIGINT received, shutting down gracefully');
  server.close(() => {
    console.log('Process terminated');
    process.exit(0);
  });
});

export default app;

/**
 * API Routes
 */

import { Router } from 'express';
import { helloRouter } from './hello.js';

const router = Router();

// API documentation endpoint
router.get('/', (req, res) => {
  res.json({
    name: '{{helper:pascalCase name}} API',
    version: '1.0.0',
    description: '{{description}}',
    endpoints: {
      '/health': 'Health check endpoint',
      '/hello': 'Greeting endpoint',
      '/docs': 'API documentation'
    }
  });
});

// Mount sub-routes
router.use('/hello', helloRouter);

export function createApiRouter(): Router {
  return router;
}

/**
 * Hello API Routes
 */

import { Router } from 'express';
import { hello } from '../services/helloService.js';

const router = Router();

/**
 * GET /api/hello
 * Get a simple greeting
 */
router.get('/', (req, res) => {
  const { name = 'World' } = req.query;

  try {
    const greeting = hello(String(name));
    res.json({
      message: greeting,
      timestamp: new Date().toISOString()
    });
  } catch (error) {
    res.status(500).json({
      error: 'Failed to generate greeting',
      message: error instanceof Error ? error.message : 'Unknown error'
    });
  }
});

/**
 * POST /api/hello
 * Create a custom greeting
 */
router.post('/', (req, res) => {
  const { name, options = {} } = req.body;

  if (!name) {
    return res.status(400).json({
      error: 'Name is required',
      message: 'Please provide a name in the request body'
    });
  }

  try {
    const greeting = hello(name, options);
    res.status(201).json({
      message: greeting,
      options,
      timestamp: new Date().toISOString()
    });
  } catch (error) {
    res.status(500).json({
      error: 'Failed to generate greeting',
      message: error instanceof Error ? error.message : 'Unknown error'
    });
  }
});

export { router as helloRouter };

/**
 * Hello Service
 */

export interface HelloOptions {
  uppercase?: boolean;
  exclamation?: boolean;
  language?: 'en' | 'es' | 'fr' | 'de';
}

export interface GreetingResponse {
  message: string;
  language: string;
  timestamp: string;
}

const greetings = {
  en: 'Hello',
  es: 'Hola',
  fr: 'Bonjour',
  de: 'Hallo'
};

/**
 * Generate a greeting message
 *
 * @param name - Name to greet
 * @param options - Additional options for formatting
 * @returns Formatted greeting message
 */
export function hello(name: string = 'World', options: HelloOptions = {}): string {
  const language = options.language || 'en';
  const greeting = greetings[language];
  let message = `${greeting}, ${name}`;

  if (options.uppercase) {
    message = message.toUpperCase();
  }

  const punctuation = options.exclamation ? '!' : '.';
  message += punctuation;

  return message;
}

/**
 * Generate a detailed greeting response
 *
 * @param name - Name to greet
 * @param options - Additional options for formatting
 * @returns Detailed greeting response
 */
export function createGreetingResponse(name: string, options: HelloOptions = {}): GreetingResponse {
  return {
    message: hello(name, options),
    language: options.language || 'en',
    timestamp: new Date().toISOString()
  };
}

/**
 * Get supported languages
 *
 * @returns Array of supported language codes
 */
export function getSupportedLanguages(): string[] {
  return Object.keys(greetings);
}

/**
 * Validate greeting options
 *
 * @param options - Options to validate
 * @returns Validation result
 */
export function validateHelloOptions(options: HelloOptions): { valid: boolean; errors: string[] } {
  const errors: string[] = [];

  if (options.language && !greetings[options.language]) {
    errors.push(`Unsupported language: ${options.language}. Supported languages: ${getSupportedLanguages().join(', ')}`);
  }

  return {
    valid: errors.length === 0,
    errors
  };
}

/**
 * Error Handling Middleware
 */

import { Request, Response, NextFunction } from 'express';

export interface AppError extends Error {
  statusCode?: number;
  status?: string;
  isOperational?: boolean;
}

/**
 * Custom error class
 */
export class CustomError extends Error implements AppError {
  statusCode: number;
  status: string;
  isOperational: boolean;

  constructor(message: string, statusCode: number = 500) {
    super(message);

    this.statusCode = statusCode;
    this.status = `${statusCode}`.startsWith('4') ? 'fail' : 'error';
    this.isOperational = true;

    Error.captureStackTrace(this, this.constructor);
  }
}

/**
 * Global error handler middleware
 */
export function errorHandler(
  error: AppError,
  req: Request,
  res: Response,
  next: NextFunction
): void {
  const statusCode = error.statusCode || 500;
  const status = error.status || 'error';

  // Log error details
  console.error('Error:', {
    message: error.message,
    stack: error.stack,
    url: req.url,
    method: req.method,
    timestamp: new Date().toISOString()
  });

  // Send error response
  res.status(statusCode).json({
    status,
    message: error.message,
    ...(process.env.NODE_ENV === 'development' && { stack: error.stack })
  });
}

/**
 * Async error wrapper
 */
export function catchAsync(fn: Function) {
  return (req: Request, res: Response, next: NextFunction) => {
    Promise.resolve(fn(req, res, next)).catch(next);
  };
}

/**
 * 404 Not Found Middleware
 */

import { Request, Response } from 'express';

/**
 * 404 Not Found handler
 */
export function notFoundHandler(req: Request, res: Response): void {
  res.status(404).json({
    status: 'fail',
    message: `Route ${req.originalUrl} not found`,
    availableRoutes: [
      '/health',
      '/api',
      '/api/hello'
    ]
  });
}

/**
 * Integration tests for the web server
 */

import { describe, it, expect, beforeAll, afterAll } from 'bun:test';
import request from 'supertest';
import app from '../src/server';

describe('{{helper:pascalCase name}} Web Server', () => {
  describe('Health Check', () => {
    it('should return 200 OK for health check', async () => {
      const response = await request(app)
        .get('/health')
        .expect(200);

      expect(response.body).toHaveProperty('status', 'ok');
      expect(response.body).toHaveProperty('timestamp');
      expect(response.body).toHaveProperty('uptime');
      expect(response.body).toHaveProperty('environment');
      expect(response.body).toHaveProperty('version', '1.0.0');
    });
  });

  describe('API Routes', () => {
    it('should return API documentation', async () => {
      const response = await request(app)
        .get('/api')
        .expect(200);

      expect(response.body).toHaveProperty('name', '{{helper:pascalCase name}} API');
      expect(response.body).toHaveProperty('version', '1.0.0');
      expect(response.body).toHaveProperty('endpoints');
    });
  });

  describe('Hello API', () => {
    it('should return default greeting', async () => {
      const response = await request(app)
        .get('/api/hello')
        .expect(200);

      expect(response.body).toHaveProperty('message');
      expect(response.body).toHaveProperty('timestamp');
      expect(response.body.message).toContain('Hello, World');
    });

    it('should return custom greeting with query parameter', async () => {
      const response = await request(app)
        .get('/api/hello?name=TypeScript')
        .expect(200);

      expect(response.body.message).toContain('Hello, TypeScript');
    });

    it('should create custom greeting with POST', async () => {
      const response = await request(app)
        .post('/api/hello')
        .send({ name: 'Bun', options: { uppercase: true, exclamation: true } })
        .expect(201);

      expect(response.body.message).toContain('HELLO, BUN!');
      expect(response.body).toHaveProperty('options');
      expect(response.body).toHaveProperty('timestamp');
    });

    it('should return 400 for missing name in POST request', async () => {
      const response = await request(app)
        .post('/api/hello')
        .send({ options: { uppercase: true } })
        .expect(400);

      expect(response.body).toHaveProperty('error', 'Name is required');
    });
  });

  describe('404 Handling', () => {
    it('should return 404 for unknown routes', async () => {
      const response = await request(app)
        .get('/unknown-route')
        .expect(404);

      expect(response.body).toHaveProperty('status', 'fail');
      expect(response.body).toHaveProperty('message');
      expect(response.body).toHaveProperty('availableRoutes');
    });
  });
});

/**
 * Unit tests for hello service
 */

import { describe, it, expect } from 'bun:test';
import { hello, createGreetingResponse, getSupportedLanguages, validateHelloOptions, type HelloOptions } from '../src/services/helloService';

describe('Hello Service', () => {
  describe('hello function', () => {
    it('should return default greeting', () => {
      expect(hello()).toBe('Hello, World.');
    });

    it('should return greeting with custom name', () => {
      expect(hello('TypeScript')).toBe('Hello, TypeScript.');
    });

    it('should return uppercase greeting', () => {
      expect(hello('TypeScript', { uppercase: true })).toBe('HELLO, TYPESCRIPT.');
    });

    it('should return greeting with exclamation', () => {
      expect(hello('TypeScript', { exclamation: true })).toBe('Hello, TypeScript!');
    });

    it('should return greeting in Spanish', () => {
      expect(hello('Mundo', { language: 'es' })).toBe('Hola, Mundo.');
    });

    it('should return greeting in French', () => {
      expect(hello('Monde', { language: 'fr' })).toBe('Bonjour, Monde.');
    });

    it('should return greeting in German', () => {
      expect(hello('Welt', { language: 'de' })).toBe('Hallo, Welt.');
    });

    it('should apply all options together', () => {
      const options: HelloOptions = {
        uppercase: true,
        exclamation: true,
        language: 'es'
      };
      expect(hello('Mundo', options)).toBe('HOLA, MUNDO!');
    });
  });

  describe('createGreetingResponse', () => {
    it('should create detailed response', () => {
      const response = createGreetingResponse('TypeScript', { language: 'fr' });

      expect(response).toHaveProperty('message', 'Bonjour, TypeScript.');
      expect(response).toHaveProperty('language', 'fr');
      expect(response).toHaveProperty('timestamp');
      expect(new Date(response.timestamp)).toBeInstanceOf(Date);
    });
  });

  describe('getSupportedLanguages', () => {
    it('should return all supported languages', () => {
      const languages = getSupportedLanguages();
      expect(languages).toEqual(['en', 'es', 'fr', 'de']);
    });
  });

  describe('validateHelloOptions', () => {
    it('should validate valid options', () => {
      const result = validateHelloOptions({ language: 'es', uppercase: true });
      expect(result.valid).toBe(true);
      expect(result.errors).toHaveLength(0);
    });

    it('should reject invalid language', () => {
      const result = validateHelloOptions({ language: 'invalid' as any });
      expect(result.valid).toBe(false);
      expect(result.errors).toContain('Unsupported language: invalid. Supported languages: en, es, fr, de');
    });

    it('should accept empty options', () => {
      const result = validateHelloOptions({});
      expect(result.valid).toBe(true);
      expect(result.errors).toHaveLength(0);
    });
  });
});
