import { StrictMode } from 'react';
import { createRoot } from 'react-dom/client';
import { App } from './App.js';
import './styles/main.css';

/**
 * Initialize and render the React application
 */
function initializeApp(): void {
  const container = document.getElementById('root');
  if (!container) {
    throw new Error('Root container not found');
  }

  const root = createRoot(container);
  root.render(
    <StrictMode>
      <App />
    </StrictMode>
  );
}

// Initialize the application
initializeApp();

export { initializeApp };

/**
 * {{helper:pascalCase name}} React Application
 *
 * {{description}}
 *
 * @author {{author}}
 * @license {{license}}
 * @version 1.0.0
 * @since {{year}}
 */

import './styles/main.css';

/**
 * Main App component
 */
function App() {
  return (
    <div className="app">
      <header>
        <h1>Welcome to {{helper:pascalCase name}}</h1>
      </header>
      <main>
        <p>Your React application is ready!</p>
      </main>
    </div>
  );
}

export default App;

/* Main styles for {{name}} */

.app {
  text-align: center;
}

h1 {
  color: #2c3e50;
  margin-bottom: 1rem;
}

p {
  color: #7f8c8d;
  font-size: 1.1rem;
}

import { defineConfig } from 'vite';
import react from '@vitejs/plugin-react';

// https://vitejs.dev/config/
export default defineConfig({
  plugins: [react()],
});

/**
 * Tests for App component
 */

import { describe, it, expect } from 'bun:test';
import { render, screen } from '@testing-library/react';
import App from '../src/App';

describe('{{helper:pascalCase name}} App', () => {
  it('should render without crashing', () => {
    render(<App />);
  });

  it('should display welcome message', () => {
    render(<App />);
    expect(screen.getByText('Welcome to {{helper:pascalCase name}}')).toBeInTheDocument();
  });

  it('should display ready message', () => {
    render(<App />);
    expect(screen.getByText('Your React application is ready!')).toBeInTheDocument();
  });
});

